    [9194] = {'[Craftsman's Writ - Runecloth Robe]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 8 Runecloth Robes and the Craftsman's Writ - Runecloth Robe - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9195] = {'장인의 계약서: 고블린 공병 폭탄', '이 계약서를 가져가는 자는 아래 작업을 완수하는 조건으로 원하는 휘장을 보상으로 받을 수 있다.', '동부 역병지대, 희망의 빛 예배당에 있는 짐노새 주인 스톤브루저에게 고블린 공병 폭탄 20개와 장인의 계약서: 고블린 공병 폭탄을 가져가야 합니다.'}, 
    [9196] = {'[Craftsman's Writ - Thorium Grenade]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 20 Thorium Grenades and the Craftsman's Writ - Thorium Grenade - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9197] = {'[Craftsman's Writ - Gnomish Battle Chicken]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 4 Gnomish Battle Chickens and the Craftsman's Writ - Gnomish Battle Chicken - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9198] = {'[Craftsman's Writ - Thorium Tube]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 14 Thorium Tubes and the Craftsman's Writ - Thorium Tube - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9200] = {'[Craftsman's Writ - Major Mana Potion]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 10 Major Mana Potions and the Craftsman's Writ - Major Mana Potion - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9201] = {'장인의 계약서: 상급 비전 보호 물약', '이 계약서를 가져는 자는 아래 작업을 완수하는 조건으로 원하는 휘장을 보상으로 받을 수 있다.', '동부 역병지대, 희망의 빛 예배당에 있는 짐노새 주인 스톤브루저에게 상급 비전 보호 물약 15개와 장인의 계약서: 상급 비전 보호 물약을 가져가야 합니다.'}, 
    [9202] = {'[Craftsman's Writ - Major Healing Potion]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 20 Major Healing Potions and the Craftsman's Writ - Major Healing Potion - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9203] = {'[Craftsman's Writ - Potion of Petrification]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 1 Potion of Petrification and the Craftsman's Writ - Potion of Petrification - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9204] = {'[Craftsman's Writ - Stonescale Eel]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 40 Stonescale Eel and the Craftsman's Writ - Stonescale Eel - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9205] = {'[Craftsman's Writ - Plated Armorfish]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 30 Plated Armorfish and the Craftsman's Writ - Plated Armorfish - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9206] = {'[Craftsman's Writ - Lightning Eel]', '[The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.', '[Deliver 30 Lightning Eel and the Craftsman's Writ - Lightning Eel - to Packmaster Stonebruiser at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9208] = {'야생의 수호: 보호의 영석', '이 잔자는 적당한 재료만 있으면 다양한 고대 트롤의 마법 물품을 만들 수 있소! 야생의 수호에 대해 들어본 적 있소? 물론 없겠지. 당신 마음속은 조잡한 엘프의 마법으로 가득할 테니까.', '줄구룹에 있는 잠들지 않는 잔자가 보호의 영석을 가져다 달라고 했습니다.'}, 
    [9209] = {'야생의 수호: 신속의 영석', '이 잔자는 적당한 재료만 있으면 다양한 고대 트롤의 마법 물품을 만들 수 있소! 야생의 수호에 대해 들어본 적 있소? 물론 없겠지. 당신 마음속은 조잡한 엘프의 마법으로 가득할 테니까.', '줄구룹에 있는 잠들지 않는 잔자가 신속의 영석을 가져다 달라고 했습니다.'}, 
    [9210] = {'야생의 수호: 집중의 영석', '이 잔자는 적당한 재료만 있으면 다양한 고대 트롤의 마법 물품을 만들 수 있소! 야생의 수호에 대해 들어본 적 있소? 물론 없겠지. 당신 마음속은 조잡한 엘프의 마법으로 가득할 테니까.', '줄구룹에 있는 잠들지 않는 잔자가 집중의 영석을 가져다 달라고 했습니다.'}, 
    [9211] = {'[The Ice Guard]', '[Ah, the Ice Guard. It is a thin sheen of protective magic that covers certain pieces of equipment. Those that do battle with creatures of frost and ice will gain increased protection from their attacks by using the Ice Guard.', '[Mataus the Wrathcaster at Light's Hope Chapel in the Eastern Plaguelands wants 10 Insignia of the Crusade and 30 gold.]'}, 
    [9213] = {'[The Shadow Guard]', '[A creation of genius! Much like the Ice Guard, the Shadow Guard employs a thin sheen of protective magic that covers certain pieces of equipment. And like the Ice Guard, the Shadow Guard is going to cost you a good amount of money.', '[Mataus the Wrathcaster at Light's Hope Chapel in the Eastern Plaguelands wants 10 Insignia of the Crusade and 30 gold.]'}, 
    [9221] = {'[Superior Armaments of Battle - Friend of the Dawn]', '[Greetings, friend. Have you insignias of the Dawn or the Crusade that you would like to redeem for items of power?', '[For 30 Insignias of the Dawn and 30 Insignias of the Crusade you may choose an item from the Argent Dawn's treasure cache.]'}, 
    [9222] = {'[Epic Armaments of Battle - Friend of the Dawn]', '[Greetings, friend. Have you insignias of the Dawn or the Crusade that you would like to redeem for items of great power?', '[For 110 Insignias of the Dawn and 110 Insignias of the Crusade you may choose an item from the Argent Dawn's treasure cache.]'}, 
    [9223] = {'희귀한 전투 장비: 여명회에 우호적', '안녕하세요! 강력한 물건과 바꿀 여명회 휘장이나 십자군 휘장이 있나요?', '여명회 휘장과 십자군 휘장을 각각 20개씩 내면 은빛 여명회의 보물 창고에서 꺼내온 물건을 하나 선택할 수 있습니다.'}, 
    [9224] = {'영웅을 위한 전투 장비: 여명회에 우호적', '안녕하세요! 굉장한 힘을 지닌 물건과 바꿀 여명회 휘장이나 십자군 휘장이 있나요?', '여명회 휘장과 십자군 휘장을 각각 75개씩 내면 은빛 여명회의 보물 창고에서 꺼내온 물건을 하나 선택할 수 있습니다.'}, 
    [9225] = {'[Epic Armaments of Battle - Revered Amongst the Dawn]', '[Greetings, friend. Have you insignias of the Dawn or the Crusade that you would like to redeem for items of great power?', '[For 45 Insignias of the Dawn and 45 Insignias of the Crusade you may choose an item from the Argent Dawn's treasure cache.]'}, 
    [9226] = {'희귀한 전투 장비: 여명회에 매우 우호적', '안녕하세요! 강력한 물건과 바꿀 여명회 휘장이나 십자군 휘장이 있나요?', '여명회 휘장과 십자군 휘장을 각각 7개씩 내면 은빛 여명회의 보물 창고에서 꺼내온 물건을 하나 선택할 수 있습니다.'}, 
    [9227] = {'[Superior Armaments of Battle - Exalted Amongst the Dawn]', '[Greetings, friend. Have you insignias of the Dawn or the Crusade that you would like to redeem for items of power?', '[For 6 Insignias of the Dawn and 6 Insignias of the Crusade you may choose an item from the Argent Dawn's treasure cache.]'}, 
    [9228] = {'[Epic Armaments of Battle - Exalted Amongst the Dawn]', '[Greetings, friend. Have you insignias of the Dawn or the Crusade that you would like to redeem for items of great power?', '[For 27 Insignias of the Dawn and 27 Insignias of the Crusade you may choose an item from the Argent Dawn's treasure cache.]'}, 
    [9229] = {'[The Fate of Ramaladni]', '[Ramaladni was a warrior of the Brotherhood - a pupil of Highlord Mograine before the Dawn even existed! When news of Mograine's death reached him, hatred and anger took over.', '[Enter Naxxramas and uncover the Fate of Ramaladni.]'}, 
    [9230] = {'[Ramaladni's Icy Grasp]', '[Fixing this band may prove to be quite challenging, <name>. You see, it was made from the very runes that form inside Scourge structures - like Naxxramas. Additional materials are also required but your main concern should be getting those frozen runes. Bring me what I need and I will restore the ring to its former glory.]', '[Korfax at Light's Hope Chapel in the Eastern Plaguelands wants you to bring him 1 Frozen Rune, 1 Blue Sapphire and 1 Arcanite Bar.]'}, 
    [9232] = {'[The Only Song I Know...]', '[You fancy yourself a hero of the Dawn, eh? I've seen our greatest soldiers get rolled over by Kel'Thuzad's plebes! How well could someone so clueless possibly fare in Naxxramas?', '[Craftsman Wilhelm at Light's Hope Chapel in the Eastern Plaguelands wants you to bring him 2 Frozen Runes, 2 Essence of Water, 2 Blue Sapphires and 30 gold pieces.]'}, 
    [9233] = {'[Omarion's Handbook]', '[Wilhelm,', '[Take Omarion's Handbook to Craftsman Wilhelm at Light's Hope Chapel in the Eastern Plaguelands.]'}, 
    [9234] = {'[Icebane Gauntlets]', '[Thanks to you and Omarion, may he rest in peace, I am now able to create Icebane gauntlets. All you need to do is bring me the materials and pay a small crafting fee.]', '[Craftsman Wilhelm at Light's Hope Chapel in the Eastern Plaguelands wants 5 Frozen Runes, 12 Thorium Bars, 2 Arcanite Bars, 2 Essences of Water and 200 gold.]'}, 
    [9235] = {'[Icebane Bracers]', '[Thanks to you and Omarion (May he rest in peace), I am now able to create Icebane bracers. All you need to do is bring me the materials and pay a small crafting fee.]', '[Craftsman Wilhelm at Light's Hope Chapel in the Eastern Plaguelands wants 4 Frozen Runes, 12 Thorium Bars, 2 Arcanite Bars, 2 Essence of Water and 200 gold.]'}, 
    [9236] = {'[Icebane Breastplate]', '[Thanks to you and Omarion (May he rest in peace), I am now able to create an Icebane breastplate. All you need to do is bring me the materials and pay a small crafting fee.]', '[Craftsman Wilhelm at Light's Hope Chapel in the Eastern Plaguelands wants 8 Frozen Runes, 16 Thorium Bars, 2 Arcanite Bars, 4 Essence of Water and 300 gold. You will also need to be of Exalted reputation with the Argent Dawn.]'}, 
    [9237] = {'[Glacial Cloak]', '[Thanks to you and Omarion, may he rest in peace, I am now able to create a Glacial cloak. All you need to do is bring me the materials and pay a small crafting fee.]', '[Craftsman Wilhelm at Light's Hope Chapel in the Eastern Plaguelands wants 5 Frozen Runes, 4 Bolts of Runecloth, 2 Essences of Water, 4 Ironweb Spider Silks and 200 gold. You must also be of Exalted reputation with the Argent Dawn.]'}, 
